sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  	var headerInputs = {};
	headerInputs['gr_header'] = inputs.gr_record;
	var headerResult = sn_fd.FlowAPI.executeAction('sn_psm_sap_int.generate_ecc_gr_header_object', headerInputs);
	var headerStatus = headerResult['__action_status__']['code'];
	if (!headerStatus) {
		outputs.gr_header = headerResult['goods_receipt_header'];

	} else {
		throw new Error("Generate ECC GR Header Object- Error : " + headerResult['__action_status__']['message']);
		}
  var gr_line=[]
  var lineInputs = {};
	lineInputs['gr_line'] = inputs.gr_record;
	var lineResult = sn_fd.FlowAPI.executeAction('sn_psm_sap_int.generate_ecc_gr_line_object', lineInputs);
	var lineStatus = lineResult['__action_status__']['code'];
	if (!lineStatus) {
      	gr_line.push(lineResult['goods_receipt_line'])
	} else {
		throw new Error("Generate ECC GR Line Object- Error : " + lineResult['__action_status__']['message']);
		}
  outputs.gr_line = gr_line;
  outputs.gm_code = '01';

})(inputs, outputs);

