sys_variable_value.value:
(function execute(inputs, outputs) {

    outputs.gr_header = new sn_psm_sap_int.PsmSapUtils().executeAction('sn_psm_sap_int.generate_idoc_gr_header_object', {
        gr_header: inputs.gr_record
    }).goods_receipt_header;

    var gr_line = [];
    var lineResult = new sn_psm_sap_int.PsmSapUtils().executeAction('sn_psm_sap_int.generate_idoc_gr_line_object', {
        gr_line: inputs.gr_record,
        isreturn: inputs.IsReturn
    }).goods_receipt_line;
    gr_line.push(lineResult);

    var dividedQuantities = new sn_psm_sap_int.PsmSapUtils().divideGRLinesBasedOnCostCenter(inputs.gr_record, lineResult);

    outputs.gr_line = dividedQuantities;
    outputs.gm_code = '01';

})(inputs, outputs);

