sys_variable_value.value:
(function execute(inputs, outputs) {

    var payload = JSON.parse(inputs.json);
    payload['DELIV_DATE'] = new sn_psm_sap_int.PsmSapUtils().convertDateToSAPFormat(payload['DELIV_DATE']);

    outputs.po_schedule = payload;

})(inputs, outputs);

