sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.erp_line_number != '') {
        var erpLineNumber = inputs.erp_line_number;
        var erpLine1 = erpLineNumber.split(",");
        outputs.erp_line_number = erpLine1;
    } else {
        throw new Error("Erp Line Number is Empty.");
    }
})(inputs, outputs);

