sys_variable_value.value:
(function execute(inputs, outputs) {
    var fields = new sn_psm_sap_int.PsmSapUtils().getCurrencyFields('sn_shop_receipt', inputs.sysid.toString());

    function getDate(grec) {
        var dg = new GlideDateTime(grec.sys_updated_on);
        var dateGiven = dg.getDate().toString().split("-");
        return dateGiven.join("");
    }
    var gr = new GlideRecord('sn_shop_receipt');
    gr.get(inputs.sysid.toString());
    outputs.posting_date = getDate(gr);
    outputs.currency_fields = fields;
})(inputs, outputs);

