sys_variable_value.value:
(function execute(inputs, outputs) {

    var payload = {};

    var headerObject = new sn_psm_sap_int.PsmSapUtils().executeAction('sn_psm_sap_int.generate_ecc_idoc_po_header_object', {
        po_header: inputs.psm_po
    }).purchase_order_header;

    if (inputs.psm_po.purchase_order_type == 'standard') {
        payload = new sn_psm_sap_int.PsmSapUtils().generateIdocStandardPOPaylaod(inputs.psm_po.sys_id.toString(), headerObject['ITEM_INTVL'], 'idoc');
    }

    outputs.e1bpekkoc = headerObject;
    outputs.e1bpekpoc = payload['polines'];
    outputs.e1bpekkn = payload['poaccounts'];
    if (payload.hasOwnProperty('poaddress')) {
        outputs.e1bpmepoaddrdelivery = payload['poaddress'];
    }

})(inputs, outputs);

