sys_variable_value.value:
(function execute(inputs, outputs) {

    var input_json = inputs.json_string;
    var delimeter_value = inputs.delimiter.toString().trim();
    var responseArray = new sn_psm_sap_int.PsmSapUtils().parseExecuteRFC(input_json, delimeter_value);
    outputs.idoc_rfc_response = responseArray.JSONOBJECT;
    outputs.total_rows = responseArray.ROWCOUNT;

})(inputs, outputs);

