sys_variable_value.value:
(function execute(inputs, outputs) {

    var payload = JSON.parse(inputs.json);

    payload['G_L_ACCT'] = new sn_psm_sap_int.PsmSapUtils().checkPadding(payload['G_L_ACCT'], 10);
    outputs.po_accounts = payload;

})(inputs, outputs);

