sys_variable_value.value:
(function execute(inputs, outputs) {

    var payload = {};

    var headerObject = new sn_psm_sap_int.PsmSapUtils().executeAction('sn_psm_sap_int.generate_ecc_idoc_update_po_header_object', {
        po_header: inputs.psm_po
    }).e1bpmepoheader;

    if (inputs.psm_po.purchase_order_type == 'standard') {
        payload = new sn_psm_sap_int.PsmSapUtils().generateIdocUpdateStandardPOPaylaod(inputs.psm_po.sys_id.toString(), "00010", 'idoc');
    }

    var headerSegment = {};
    var poHeader = {};
    poHeader["PO_NUMBER"] = headerObject["PO_NUMBER"];
    headerSegment["E1BPMEPOHEADER"] = headerObject;
    headerSegment["E1BPMEPOITEM"] = payload['polines'];
    headerSegment["PURCHASEORDER"] = headerObject["PO_NUMBER"];

    outputs.E1PORDCH = headerSegment;

})(inputs, outputs);

